int GcdEx(const int a, const int b, int& x, int& y) {
  if (!a) {
    x = 0;
    y = 1;
    return b;
  }
  int x1, y1;
  int d = GcdEx(b % a, a, x1, y1);
  x = y1 - (b / a) * x1;
  y = x1;
  return d;
}
int CalcInverseNumber(const int number, const int mod) {
  int x, y;
  int g = GcdEx(number, mod, x, y);
  if (g != 1) {
    return -1;
  }
  return x = (x % mod + mod) % mod;
}
