T FactorialWithMod(T n, T mod) {
  if (n >= mod) {
    return 0;
  }
  T result = 1;
  while (n > 0) {
    result = (result * n) % mod;
    --n;
  }
  return result;
}
template <class T1, class T2>
T1 BinPow(T1 value, T2 extent) {
  T1 res = 1;
  while (extent > 0) {
    if (extent & 1) {
      res *= value;
    }
    extent >>= 1;
    value *= value;
  }
  return res;
}
template <class T1, class T2>
T1 BinPowWithMod(T1 value, T2 extent, T1 mod) {
  T1 res = 1;
  value %= mod;
  while (extent > 0) {
    if (extent & 1) {
      res = (value * res) % mod;
    }
    extent >>= 1;
    value = (value * value) % mod;
  }
  return res;
}
