TVector<int> Factorize(int value) {
  TVector<int> res;
  for (int i = 2; i * i <= value; i++) {
    while (value % i == 0) {
      res.push_back(i);
      value /= i;
    }
  }
  if (value > 1) {
    res.push_back(value);
