int solve() {
  int t;
  read(t);
  while (t--) {
    int n, k;
    read(n, k);
    TVector<char> v;
    NVector::Read(v, n);
    TVector<pair<int, int>> ps;
    ps.reserve(n);
    for (int i = 0; i < v.size(); i++) {
      if (v[i] == 'L') {
        if (ps.empty() || v[i - 1] == 'W') {
          ps.push_back({1, i});
        } else {
          ps[ps.size() - 1].first++;
        }
      }
    }
    NVector::Sort(ps);
    for (int i = 0; i < ps.size() && k; i++) {
      if (ps[i].second == 0 || ps[i].second + ps[i].first == v.size()) {
        continue;
      }
      for (int j = ps[i].second; k && j < ps[i].second + ps[i].first; j++) {
        k--;
        v[j] = 'W';
      }
    }
    for (int i = 1; k && i < v.size(); i++) {
      if (v[i] == 'L' && v[i - 1] == 'W') {
        k--;
        v[i] = 'W';
      }
    }
    for (int i = v.size() - 2; k && i >= 0; i--) {
      if (v[i] == 'L' && v[i + 1] == 'W') {
        k--;
        v[i] = 'W';
      }
    }
    for (int i = v.size() - 1; k && i >= 0; i--) {
      if (v[i] == 'L') {
        k--;
        v[i] = 'W';
      }
    }
    int ans = (v[0] == 'W' ? 1 : 0);
    for (int i = 1; i < v.size(); i++) {
      if (v[i] == 'W') {
        ans++;
        if (v[i - 1] == 'W') {
          ans++;
        }
      }
    }
    cout << ans << '\n';
  }
  return 0;
}

