public class B {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int t = sc.nextInt();
		while(t-->0) {
			int n = sc.nextInt();
			int k = sc.nextInt();
			char[] a = sc.next().toCharArray();

			int score = 0;
			int[] runs = new int[n+1]; //array of gap sizes
			int last_win = -1;
			for(int i = 0; i < n; i++) {
				if(a[i] == 'W') {
					if(last_win != -1 && last_win == i-1) {
						score += 2;
					} else {
						score += 1;
					}

					if(last_win != -1 && last_win != i-1) {
						runs[i-last_win-1]++; //gap size
					}
					last_win = i;
				}
			}

			if(last_win == -1) {
				System.out.println(max(0, 2*k-1));
			} else {
				score += 2*k;
				int rem = k;
				for(int i = 1; i <= n; i++) {
					while(rem >= i && runs[i] > 0) {
						runs[i]--;
						rem -= i;
						score++;
					}
				}

				System.out.println(min(score, 2*n-1));
			}
		}
	}
}

