def cheatScore(s, n, k):
    lose = 0
    score = 0
    start = 0
    tmp = []
    flag = 0
    Alllose = 1
    for c in s:
        if c != 'W':
            continue
        else:
            Alllose = 0
            break
    if Alllose:
        return max(0, 2*min(n, k)-1)

    for i in range(len(s)):
        if i == 0:
            if s[i] == 'L':
                count = 1
                flag = 1
            else:
                continue
        if s[i] == 'L':
            if s[i-1] == 'W':
                count = 1
            elif s[i-1] == 'L':
                count += 1
        else:
            if s[i-1] == 'L':
                tmp.append(count)
            else:
                continue
    stEnd = 0
    if flag:
        stEnd += tmp.pop(0)
    if s[-1] == 'L':
        stEnd += count
    tmp = sorted(tmp)
    #print(s, stEnd, tmp)

    for i in range(len(s)):
        if i == 0:
            if s[i] == 'W':
                score += 1
            else:
                lose += 1
            continue
        if s[i] == 'W':
            if s[i-1] == 'W':
                score += 2
            else:
                score += 1
        else:
            lose += 1
    score += 2 * min(lose, k)
    bridge = 0
    leave = 0
    for gap in tmp:
        if k >= gap:
            bridge += 1
            k -= gap
        else:
            leave += gap
    score += bridge
    return score

