				str.append(2 * k - 1 +"\n");
				continue;
			}

			for(char c : arr) {

				if(c == 'W') 
				 break;
				fw++;
			}

			for(int i = n - 1; i >= 0; i--) {

				if(arr[i] =='W') break;
				lw--;
			}

			ArrayList<Integer> list = new ArrayList<>();

			long ans = 0;
			boolean W = false;
			int count = 0;

			for(int i = fw; i <= lw; i++) {
				if(arr[i] == 'W') {
					if(count > 0 ) list.add(count);
					count = 0;
					if(W)  ans+=2;
					else ans++;

					W = true;
				}else {

					count++;

					W = false;
				}

			}



			Collections.sort(list);

			int stop = 0;

			for(int i = 0; i < list.size(); i++) {
				int cur = list.get(i);

				if(cur <= k) {
					stop++;

					ans+= 2 * cur + 1;

					k -= cur;


				}else break;

			}

			if(stop < list.size()) {

				ans += 2 * k;

				k = 0;

			}


			int onRight = n - 1 - lw;

			int use = Math.min(k, onRight);

			ans += 2 * use;

			k -= use;

			int onLeft = fw;

			use = Math.min(k, onLeft);

			ans += 2 * use;

			str.append(ans+"\n");







		}
		System.out.println(str);
     }





