def gcd(a, b):
    if b == 0:
        return a
    else:
        return gcd(b, a % b)


def pw(a, b, md):
    result = 1
    while (b > 0):
        if (b % 2 == 1):
            result *= a
            result %= md
        a *= a
        a %= md
        b //= 2
    return result % md


