import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.Collections;
import java.util.StringTokenizer;

public class Solution {
    public static void main(String[] args) throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));
        int t = Integer.parseInt(reader.readLine());
        StringBuilder ans = new StringBuilder();
        while (t-->0) {
            StringTokenizer sToken = new StringTokenizer(reader.readLine());
            int n = Integer.parseInt(sToken.nextToken());
            int k = Integer.parseInt(sToken.nextToken());
            char[] arr = reader.readLine().toCharArray();
            int first = -1, last = 0;
            ArrayList<Integer> list = new ArrayList<>();
            int result = 0;
            for (int i=0; i<n; i++) {
                if (arr[i]=='W') {
                    result++;
                    if (i>0 && arr[i-1]=='W') result++;
                    if (last==0) {
                        if (first==-1) first = 0;
                    } else {
                        if (first==-1) first = last;
                        else list.add(last);
                    }
                    last = 0;
                } else last++;
            }
            if (last==n) {
                if (k==0) ans.append("0\n");
                else ans.append(1+((k-1)*2)).append("\n");
                continue;
            }
            Collections.sort(list);
            int index = 0;
            int[] res = new int[k+1];
            res[0] = result;
            for (int elem:list) {
                if (index+elem>k) {
                    res[k] = res[index]+((k-index)*2);
                    index = k; break;
                } else {
                    res[index+elem] = res[index]+(elem*2)+1;
                    index+=elem;
                }
            }
            int answer = res[index];
            if (index<k) {
                int diff = k - index;
                answer+=(Math.min(diff, first+last)*2);
            }
            ans.append(answer).append("\n");
        }
        System.out.println(ans);
    }
}

