    static int bpm(ArrayList<Integer> adj[], int n){
        int pair[] = new int[2*n+1];
        int dist[] = new int[n+1];
        int match = 0;
        while(bfs(pair,dist,n,adj)){
            for(int v = 1; v <= n; v++){
                if(pair[v]==0 && dfs(v,pair,dist,n,adj)){
                    match++;
                }
            }
        }
        return match;
    }

    static boolean bfs(int pair[], int dist[], int n, ArrayList<Integer> adj[]){
        Deque<Integer> q = new LinkedList<>();
        for(int i=1;i<=n;i++){
            if(pair[i]==0){
                dist[i] = 0;
                q.offerLast(i);
            }
            else{
                dist[i] = Integer.MAX_VALUE;
            }
        }
        dist[0] = Integer.MAX_VALUE;
        while(q.size()>0){
            int v = q.pollFirst();
            if(dist[v]<dist[0]){
                for(int a:adj[v]){
                    if(dist[pair[a]]==Integer.MAX_VALUE){
                        dist[pair[a]] = dist[v] + 1;
                        q.offerLast(pair[a]);
                    }
                }
            }
        }
        return dist[0]!=Integer.MAX_VALUE;
    }

    static boolean dfs(int v, int pair[], int dist[], int n, ArrayList<Integer> adj[]){
        if(v==0)
            return true;
        for(int a:adj[v]){
            if(dist[pair[a]]==dist[v]+1){
                if(dfs(pair[a],pair,dist,n,adj)){
                    pair[a] = v;
                    pair[v] = a;
                    return true;
                }
            }
        }
        dist[v] = Integer.MAX_VALUE;
        return false;
    }

