import java.util.ArrayList;
import java.util.List;
import java.util.PriorityQueue;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int t = sc.nextInt();

        while(t>0) {
            t--;

            int n = sc.nextInt();
            int k = sc.nextInt();
            String s = sc.next();

            int maxScore = 1+2*(n-1);

            List<NodeNum> list = new ArrayList<>();
            char prev = 'W';
            int start=0;
            int end=0;
            for(int i=0;i<=s.length();i++) {
                if(i==s.length()) {
                    if(prev=='W') {
                        continue;
                    }
                    end = i-1;
                    NodeNum one = new NodeNum();
                    one.num = end-start+1;
                    one.score = 1+2*(one.num-1);
                    one.extraScore = 0;

                    if(start!=0) {
                        one.extraScore++;
                        one.score++;
                    }
                    if(end!=s.length()-1) {
                        one.extraScore++;
                        one.score++;
                    }
                    list.add(one);
                    break;
                }

                if(s.charAt(i)=='W' && prev=='L') {
                    prev = 'W';

                    end = i-1;
                    NodeNum one = new NodeNum();
                    one.num = end-start+1;
                    one.score = 1+2*(one.num-1);
                    one.extraScore = 0;

                    if(start!=0) {
                        one.extraScore++;
                        one.score++;
                    }
                    if(end!=s.length()-1) {
                        one.extraScore++;
                        one.score++;
                    }
                    list.add(one);
                    continue;
                }

                if(s.charAt(i)=='W' && prev=='W') {
                    prev = 'W';
                    continue;
                }

                if(s.charAt(i)=='L' && prev=='W') {
                    prev = 'L';
                    start = i;
                    continue;
                }

                if(s.charAt(i)=='L' && prev=='L') {
                    prev = 'L';
                    continue;
                }
            }

            PriorityQueue<NodeNum> queue = new PriorityQueue<>((o1,o2)-> {
                if(o1.extraScore!=o2.extraScore) {
                    return Integer.compare(o2.extraScore, o1.extraScore);
                }
                if(o1.num!=o2.num) {
                    return Integer.compare(o1.num, o2.num);
                } else {
                    return Integer.compare(o2.score, o1.score);
                }
            });

            for(int i=0;i<list.size();i++) {
                queue.add(list.get(i));
            }

            int res = 0;
            while(k!=0 && !queue.isEmpty()) {
                NodeNum one = queue.poll();
                if(one.num<=k) {
                    k -= one.num;
                    continue;
                }

                if((1+2*(one.num-1)) == one.score) {
                    res = one.score - (1+2*(k-1));
                } else {
                    res = one.score - 2*k;
                }
                k=0;
            }

            maxScore-=res;
            while(!queue.isEmpty()) {
                NodeNum one = queue.poll();
                maxScore-=one.score;
            }

            System.out.println(maxScore);
        }
    }
}

