import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.Comparator;
import java.util.PriorityQueue;
import java.util.StringTokenizer;

public class Task2 {

    public static void main(String[] args) throws IOException {
        new Task2().solve();
    }

    private void solve() throws IOException {
        BufferedReader f = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter out = new PrintWriter(System.out);

        int t = Integer.parseInt(f.readLine());
        for (int t1 = 0; t1 < t; t1++) {
            StringTokenizer tokenizer = new StringTokenizer(f.readLine());
            int n = Integer.parseInt(tokenizer.nextToken());
            int k = Integer.parseInt(tokenizer.nextToken());
            char[] seq = f.readLine().toCharArray();
            int index = 0;

            int lastW = -1;
            PriorityQueue<int[]> pq = new PriorityQueue<int[]>(new ArrayComparator());
            for (int i = 0; i < n; i++) {
                if (seq[i] == 'W') {
                    if (lastW != -1 && lastW != i - 1) {
                        int[] ar = {i, i - lastW - 1};
                        pq.add(ar);
                    }
                    lastW = i;
                }
            }

            while (!pq.isEmpty() && pq.peek()[1] <= k) {
                int[] dist = pq.remove();
                for (int i = dist[0] - 1; dist[0] - i <= dist[1]; i--) {
                    seq[i] = 'W';
                    k--;
                }
            }

            boolean containsW = false;

            int res = 0;
            int bad = 0;
            for (int i = 0; i < n; i++) {
                if (seq[i] == 'W') {
                    containsW = true;
                    res++;
                    if (i != 0 && seq[i - 1] == 'W') res++;
                } else {
                    bad++;
                }
            }
            if (containsW) {
                res += Math.min(bad, k) * 2;
            } else {
                if (k != 0) {
                    res += Math.min(bad, k) * 2;
                    res--;
                }
            }


            out.println(res);
        }

        out.close();
    }

    private static class ArrayComparator implements Comparator<int[]> {

        @Override
        public int compare(int[] o1, int[] o2) {
            return Integer.compare(o1[1], o2[1]);
        }
    }
}

