import java.util.Scanner;

public class Main {

    static class Node {
        int start ,end ,score;
        Node (int start) { this.start = start; }
        void setEnd (int end) {
            this.end = end;
            this.score = 2*(end - start + 1) - 1;
        }
    }
    static int optimalScore (char[] arr ,int k) {
        int computedScore = 0 ,s = 0 ,e = arr.length - 1 ,left = 0;
        while (s <= e && arr[s] == 'L') {
            left++;
            s++;
        }
        while (e >= s && arr[e] == 'L') {
            left++;
            e--;
        }
        if (left == arr.length)
            return (k > 0) ? (2*k - 1) : 0;
        int[] pref = new int[k];
        for (int i = s; i <= e; i++) {
            if (arr[i] == 'W') {
                Node component = new Node(i);
                while (i < e && arr[i + 1] == 'W')
                    i++;
                component.setEnd(i);
                computedScore += component.score;
            }
            else {
                int j = i;
                while (arr[i + 1] == 'L')
                    i++;
                if (k > (i - j))   pref[i - j]++;
                else    left += i - j + 1;
            }
        }
        if (k == 0)
            return computedScore;
        for (int i = 0; i < pref.length; i++) {
            if (pref[i] == 0)   continue;
            if (k >= pref[i] * (i + 1)) {
                computedScore += pref[i] * (2*(i + 1) + 1);
                k -= pref[i] * (i + 1);
            }
            else {
                if (k < (i + 1))
                    computedScore += 2*k;
                else {
                    int mod = k % (i + 1);
                    int n = (k - mod)/(i + 1);
                    computedScore += n * (2*(i + 1) + 1) + 2*mod;
                }
                return computedScore;
            }
        }
        return (k == 0) ? computedScore : computedScore + Math.min(k ,left) * 2;
    }

    public static void main(String[] args) {
        Scanner input = new Scanner(System.in);
        int t = input.nextInt();
        for (int i = 0; i < t; i++) {
            int n = input.nextInt();
            char[] arr = new char[n];
            int k = input.nextInt();
            String str = input.next();
            for (int j = 0; j < n; j++)
                arr[j] = str.charAt(j);
