public class B {
    static class Scan {
        BufferedReader br;
        StringTokenizer st;

        public Scan() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        public Scan(String f) {
            try {
                br = new BufferedReader(new FileReader(f));
            } catch (Exception ignored) {
            }
        }

        public String next() {
            if (st == null || !st.hasMoreElements())
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (Exception ignored) {
                }
            return st.nextToken();
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }

        public long nextLong() {
            return Long.parseLong(next());
        }

        public double nextDouble() {
            return Double.parseDouble(next());
        }

        public String nextLine() {
            try {
                return br.readLine();
            } catch (Exception ignored) {
                return "";
            }
        }
    }
    public static void main(String[] args) {
        Scan scan = new Scan();
        int T = scan.nextInt();
        for(int t = 0; t < T; t++){
            int n = scan.nextInt();
            int k = scan.nextInt();
            char[] chars = scan.next().toCharArray();
            int[] arr = new int[n];
            int lastW = -1;
            int fW = -1;
            int numW = 0;
            PriorityQueue<Integer> pq = new PriorityQueue<>(n+5);
            int score = 0;
            for(int i = 0; i < n; i++) {
                arr[i] = chars[i] == 'L' ? 0:1;
                numW+=arr[i];
                if(lastW < 0){
                    if(arr[i] == 1){
                        lastW = i;
                        fW=i;
                        score=1;
                    }
                } else {
                    if(arr[i] == 1){
                        pq.add(i-lastW-1);
                        lastW=i;
                        if(arr[i-1]==1){
                            score+=2;
                        } else score++;
                    }
                }
            }
            while(!pq.isEmpty() && k>0){
                int i = pq.poll();
                if (i<=k && i!=0){
                    k-=i;
                    numW+=i;
                    score+=2*i+1;
                }
                if (i>k){
                    pq.add(i);
                    break;
                }
            }
            if(numW==0){
                score=Math.max(2*k-1,0);
            } else {
                score+=2*Math.min(n-numW,k);
            }
            System.out.println(score);
        }
    }
}

