public class B{

    static class Pair implements Comparable<Pair>{
        int idx, dist;
        public Pair(int idx, int dist){
            this.idx=idx;
            this.dist=dist;
        }
        @Override
        public int compareTo(Pair p){
            return this.dist-p.dist;
        }
    }

    public static void main(String[] args){
        FastReader sc=new FastReader();
        int t=sc.nextInt();
        while(t-->0){
            int n=sc.nextInt(), k=sc.nextInt();
            char[] games=sc.nextLine().toCharArray();
            int score=0;
            ArrayList<Pair> distances=new ArrayList<>();
            int prev=-1;
            for(int i=0; i<n; ++i){
               if(games[i]=='W'){
                if(prev==-1){
                    prev=i;
                    continue;
                }
                else{
                    distances.add(new Pair(prev, i-prev-1));
                    prev=i;
                }
               }
            }
            Collections.sort(distances);
            for(int i=0; i<distances.size(); ++i){
                if(k==0) break;
                Pair p=distances.get(i);
                int idx=p.idx;
                int dist=p.dist;
                for(int j=1; j<=dist; ++j){
                    if(k==0) break;
                    games[idx+j]='W';
                    k--;
                }
            }
            if(k!=0){
                int lastIdx=-1;
                int firstIdx=-1;
                for(int i=n-1; i>=0; --i){
                    if(games[i]=='W'){
                        lastIdx=i;
                        break;
                    }
                }
                if(lastIdx==-1){
                    score+=1+(k-1)*2;
                    System.out.println(score);
                    continue;
                }
                else{
                    for(int i=lastIdx+1; i<n; ++i){
                        if(k==0) break;
                        games[i]='W';
                        k--;
                    }
                    for(int i=0; i<n; ++i){
                        if(games[i]=='W'){
                            firstIdx=i;
                            break;
                        }
                    }
                    if(k!=0){
                        for(int i=firstIdx-1; i>=0; --i){
                            if(k==0) break;
                            games[i]='W';
                            k--;
                        }
                    }
                }
            }
            //calculate score
            char prevCh='L';
            for(int i=0; i<n; ++i){
                if(prevCh=='W' && games[i]=='W') score+=2;
                else if(games[i]=='W') score+=1;
                prevCh=games[i];
            }
            System.out.println(score);

        }
    }

    static class FastReader{
            BufferedReader br; StringTokenizer st;
            public FastReader(){
                br = new BufferedReader(new InputStreamReader(System.in));
            }
            String next(){
                while (st == null || !st.hasMoreElements()) {
                    try {
                        st = new StringTokenizer(br.readLine());
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }
                return st.nextToken();
            }
            int nextInt() {
                return Integer.parseInt(next());
            }
            long nextLong() {
                return Long.parseLong(next());
            }
            double nextDouble() {
                return Double.parseDouble(next());
            }
            String nextLine() {
                String str = "";
                try {
                    str = br.readLine();
                } catch (IOException e) {
                    e.printStackTrace();
                }
                return str;
            }
        }
}

