function fun(gameStr,count) {
    var numL = 0;
    var numW = 0;
    var WArr = [];
    var spaceArr = [];
    var minInd = 0;
    var maxInd = 0;
    var currentScore = 0;

    //全L
    if(gameStr.indexOf("W")===-1){
        if(count===0){
            return 0;
        }
        if(count>gameStr.length){
            return gameStr.length*2-1;
        }else {
            return count*2-1;
        }
    }

    for(var i=0;i<=gameStr.length;i++){
        if(gameStr[i]==="W"){
            numW++;
        }else if(gameStr[i]!=="W" && numW!==0){
            WArr.push(numW);
            numW = 0;
        }
    }

    // 计算当前分数
    for(var i=0;i<WArr.length;i++){
        if(WArr[i] === 1){
            currentScore = currentScore+1;
        }else {
            currentScore = currentScore + WArr[i]*2-1;
        }
    }

    for(var i=0;i<gameStr.length;i++){
        if(gameStr[i]==="W"){
            minInd = i;
            break;
        }
    }
    for(var i=gameStr.length;i>0;i--){
        if(gameStr[i]==="W"){
            maxInd = i;
            break;
        }
    }

    for(var i=minInd;i<=maxInd;i++){
        if(gameStr[i]==="L"){
            numL++;
        }else if(gameStr[i]!=="L" && numL!==0){
            spaceArr.push(numL);
            numL = 0;
        }
    }

    spaceArr.sort((a,b)=>a-b);

    var score = 0;
    for(var j=0;j<spaceArr.length;j++){
        if(spaceArr[j]<=count && count!==0){
            score = score+2*spaceArr[j]+1;
            count = count-spaceArr[j];
        }else if(spaceArr[j]>count && count!==0) {
            score = score+2*count;
            count = 0;
        }
    }
    if(count>0){
        score = score+2*count;
    }
    score = score+currentScore;

    if(score>=gameStr.length*2-1){
        return gameStr.length*2-1;
    }else {
        return score;
    }
}

