public class B {

   private static final String INPUT_FILE_PATH = "";

   void solve() {
      int t = in.nextInt();
      while (t-- > 0) {
         int n = in.nextInt(), k = in.nextInt();
         String s = in.nextToken();
         ArrayList<Streak> streaks = new ArrayList();
         for (int i = 0; i < n; ) {
            if (s.charAt(i) == 'L') {
               i++;
               continue;
            }
            int j = i + 1;
            while (j < n && s.charAt(j) == 'L') j++;
            if (j < n && j != i + 1) streaks.add(new Streak(i + 1, j - i - 1));
            i = j;
         }

         char[] pick = s.toCharArray();
         Collections.sort(streaks);
         for (Streak streak : streaks) {
            if (streak.length <= k) {
               k -= streak.length;
               for (int i = streak.start; i < streak.start + streak.length; i++) pick[i] = 'W';
            }
         }
         boolean seenW = false;
         int firstWIndex = n;
         for (int i = 0; i < n; i++) {
            if (pick[i] == 'W') {
               seenW = true;
               firstWIndex = Math.min(firstWIndex, i);
               continue;
            }
            if (seenW && k > 0) {
               pick[i] = 'W';
               k--;
            }
         }
         for (int i = firstWIndex - 1; i >= 0; i--) {
            if (pick[i] == 'L' && k > 0) {
               pick[i] = 'W';
               k--;
            }
         }

         int ans = 0;
         for (int i = 0; i < n; i++) {
            if (pick[i] == 'W') {
               ans++;
               if (i > 0 && pick[i - 1] == 'W') ans++;
            }
         }
         out.println(ans);
      }
   }

   class Streak implements Comparable<Streak> {
      int start, length;
      Streak(int start, int length) {
         this.start = start;
         this.length = length;
      }

      @Override
      public int compareTo(Streak streak) {
         return Integer.compare(this.length, streak.length);
      }
   }

   private final InputReader in;
   private final PrintWriter out;

   private B(InputReader in, PrintWriter out) {
      this.in = in;
      this.out = out;
   }

   private static class InputReader {
      private BufferedReader br;
      private StringTokenizer st;

      public InputReader(InputStream inputStream) {
         this.br = new BufferedReader(new InputStreamReader(inputStream), 32768);
         this.st = null;
      }

      public String nextToken() {
         while (st == null || !st.hasMoreTokens()) {
            try {
               st = new StringTokenizer(br.readLine());
            } catch (IOException e) {
               throw new RuntimeException(e);
            }
         }
         return st.nextToken();
      }

      public int nextInt() {
         return Integer.parseInt(nextToken());
      }

      public long nextLong() {
         return Long.parseLong(nextToken());
      }

      public double nextDouble() {
         return Double.parseDouble(nextToken());
      }
   }

   public static void main(String[] args) throws Exception {
      InputStream inputStream = INPUT_FILE_PATH.isEmpty()
         ? System.in
         : new FileInputStream(new File(INPUT_FILE_PATH));
      OutputStream outputStream = System.out;

      InputReader inputReader = new InputReader(inputStream);
      PrintWriter printWriter = new PrintWriter(outputStream);

      new B(inputReader, printWriter).solve();

      printWriter.close();
   }

}

