def chess(s, k):
    l_count = s.count('L')
    if l_count <= k:
        return len(s)*2 - 1
    interval_count = 1
    intervals = []
    start = 0
    wins = s.count('W') + k
    for i in range(len(s)):
        if s[i] == 'W':
            start = i
            break
    is_winning = True
    curr_lose_size = 0
    for i in range(start, len(s)):
        if is_winning and s[i] == 'L':
            is_winning = False
            curr_lose_size += 1
        elif not is_winning and s[i] == 'L':
            curr_lose_size += 1
        elif not is_winning and s[i] == 'W':
            is_winning = True
            interval_count += 1
            intervals.append(curr_lose_size)
            curr_lose_size = 0
    intervals.sort()
    for i in intervals:
        k -= i
        if k >= 0:
            interval_count -= 1
        else:
            break
    return wins*2 - interval_count if wins > 0 else 0


