def solve(n, k, games):
    W, L = "W", "L"

    if n == 1:
        if games[0] == L and k == 0:
            return 0
        return 1

    gl = []
    wins = 0
    scores = 0
    if games[0] == W:
        scores += 1
        wins += 1
    else:
        gl.append(1)
    for i in range(1, n):
        if games[i] == W:
            wins += 1
            if games[i-1] == W:
                scores += 2
            else:
                scores += 1
        else:
            if games[i-1] == W:
                gl.append(1)
            else:
                gl[-1] += 1

    m = wins + k
    if m >= n:
        return 2*n-1
    if k == 0:
        return scores
    if wins == 0:
        return 2*k-1

    if games[0] == L:
        gl.pop(0)
    if games[-1] == L:
        gl.pop()

    a = 0
    gl.sort()
    c = 0
    for i in range(len(gl)):
        c += gl[i]
        if c <= k:
            a += 1
        else:
            break
    return scores + a + 2*k


