const int MOD = 1e9 + 7;
void solve() {
  long long int n, k;
  cin >> n >> k;
  string s;
  cin >> s;
  long long int idx = 0;
  while ((s[idx] == 'L') && (idx < n)) {
    idx++;
  }
  if (idx == n) {
    cout << max(0LL, 2 * min(n, k) - 1) << "\n";
    return;
  }
  long long int loss = 0;
  long long int win = 0;
  long long int ans = 0;
  vector<long long int> cnt;
  for (long long int i = idx; i < n; i++) {
    if (s[i] == 'L') {
      loss++;
      win = 0;
    } else {
      if (loss > 0) {
        cnt.push_back(loss);
        loss = 0;
      }
      if (win == 0) {
        ans += 1;
      } else {
        ans += 2;
      }
      win++;
    }
  }
  long long int cntsi = cnt.size();
  sort(cnt.begin(), cnt.end());
  for (long long int i = 0; i < cntsi; i++) {
    if (k < cnt[i]) {
      ans += k * 2;
      k = 0;
    } else {
      ans += cnt[i] * 2 + 1;
      k = k - cnt[i];
    }
    if (k == 0) {
      break;
    }
  }
  if (k > 0) {
    ans = min(ans + k * 2, 2 * n - 1);
  }
  cout << ans << "\n";
}
