            int last_win = -1;
            for(int i = 0; i < n; i++) {
                if(a[i] == 'W') {
                    if(last_win != -1 && last_win == i-1) {
                        score += 2;
                    } else {
                        score += 1;
                    }

                    if(last_win != -1 && last_win != i-1) {
                        runs[i-last_win-1]++;
                    }
                    last_win = i;
                }
            }

            if(last_win == -1) {
                System.out.println(max(0, 2*k-1));
