                for(int i = 1; i <= n; i++) {
                    while(rem >= i && runs[i] > 0) {
                        runs[i]--;
                        rem -= i;
                        score++;
                    }
                }

                System.out.println(min(score, 2*n-1));
            }
        }
    }

