	public static class Gap implements Comparable<Gap> {
		int length;
		boolean begin;
		boolean end;

		public Gap(int length, boolean begin, boolean end) {
			this.length = length;
			this.begin = begin;
			this.end = end;
		}

		@Override
		public int compareTo(Gap o) {
			if (length != o.length) return length - o.length;
			else return begin || end ? 1 : -1;
		}

		@Override
		public String toString() {
			return length + " " + begin + " " + end;
		}
	}

	public static class FastIO {
		private InputStream dis;
		private byte[] buffer = new byte[1 << 17];
		private int pointer = 0;

		public FastIO(String fileName) throws IOException {
			dis = new FileInputStream(fileName);
		}

		public FastIO(InputStream is) throws IOException {
			dis = is;
		}

		public int nextInt() throws IOException {
			int ret = 0;
			byte b;

			do {
				b = nextByte();
			} while (b <= ' ');

			boolean negative = false;
			if (b == '-') {
				negative = true;
				b = nextByte();
			}

			while (b >= '0' && b <= '9') {
				ret = 10 * ret + b - '0';
				b = nextByte();
			}

			return (negative) ? -ret : ret;
		}

		public long nextLong() throws IOException {
			long ret = 0;
			byte b;

			do {
				b = nextByte();
			} while (b <= ' ');

			boolean negative = false;
			if (b == '-') {
				negative = true;
				b = nextByte();
			}

			while (b >= '0' && b <= '9') {
				ret = 10 * ret + b - '0';
				b = nextByte();
			}

			return (negative) ? -ret : ret;
		}

		public byte nextByte() throws IOException {
			if (pointer == buffer.length) {
				dis.read(buffer, 0, buffer.length);
				pointer = 0;
			}
			return buffer[pointer++];
		}

		public String next() throws IOException {
			StringBuffer ret = new StringBuffer();
			byte b;

			do {
				b = nextByte();
			} while (b <= ' ');

			while (b > ' ') {
				ret.appendCodePoint(b);
				b = nextByte();
			}

			return ret.toString();
		}

		public void close() throws IOException {
			dis.close();
		}
	}
}

