void Solve() {
  int n, k;
  cin >> n >> k;
  string s;
  cin >> s;
  vector<int> fasele;
  int l = -1;
  for (long long i = 0; i <= s.length() - 1; i++) {
    if (s[i] == 'W') {
      if (i - l > 1 && l != -1) fasele.push_back(i - l - 1);
      l = i;
    }
  }
  long long score = s[0] == 'W';
  for (long long i = 1; i <= s.length() - 1; i++) {
    if (s[i] == 'W') {
      if (s[i - 1] == 'W')
        score += 2;
      else
        score += 1;
    }
  }
  sort(fasele.begin(), fasele.end());
  for (long long i = 0; i <= (int)fasele.size() - 1; i++)
    if (fasele[i] <= k) {
      k -= fasele[i];
      score += fasele[i] * 2 + 1;
    } else if (k > 0) {
      score += k * 2;
      k = 0;
    } else
      break;
  if (k > 0) {
    int first = -1, last = -1;
    for (long long i = 0; i <= s.length() - 1; i++)
      if (s[i] == 'W') {
        first = i;
        break;
      }
    for (long long i = s.length() - 1; i >= 0; i--)
      if (s[i] == 'W') {
        last = i;
        break;
      }
    if (first != -1) {
      score += min(k, (s.length() - last - 1)) * 2;
      k -= s.length() - last - 1;
      if (k > 0) score += min(k, first) * 2;
    } else {
      score += k * 2 - 1;
    }
  }
  cout << score << '\n';
}
