		Scanner sc = new Scanner(System.in);
		int t=sc.nextInt();
		while(t-->0) {
			int n=sc.nextInt();
			int k=sc.nextInt();
			String s=sc.next();
			int ans=0;
			int loss=0;
			for(int i=0;i<n;i++) {
				if(s.charAt(i)=='W') {
					++ans;
					if(i>0 && s.charAt(i-1)=='W')
						++ans;
				}
				else {
					++loss;
				}
			}
			if(k==0) {
				System.out.println(ans);
				continue;
			}
			if(ans==0) {
				System.out.println(2*k-1);
				continue;
			}
			if(k>=loss) {
				System.out.println(2*n-1);
				continue;
			}
			int tmp=Integer.MIN_VALUE;
			PriorityQueue<Integer>pq=new PriorityQueue<>();
			for(int i=0;i<n;i++) {
				if(s.charAt(i)=='W') {
					if(tmp>0) {
						pq.add(tmp);
					}
					tmp=0;
				}
				else {
					++tmp;
				}
			}
			ans+=2*k;
			while(!pq.isEmpty()) {
				int op = pq.poll();
				if(op>k)
					break;
				k-=op;
				++ans;
			}
			System.out.println(ans);
		}
	}
