			int N = sc.nextInt();
			int K = sc.nextInt();

			StringBuilder s = new StringBuilder(sc.nextLine());

			if(check(s)) {
				for(int i = 0; i < N && K > 0; i++, K--) {
					s.replace(i, i+1, "W");
				}
			} else {

				PriorityQueue<tup> pq = new PriorityQueue<>(new tup());

