public class Solution1427B {
  public static void main(String[] args) {
    InputReader in = new InputReader(System.in);
    PrintWriter out = new PrintWriter(System.out);
    Solver1427B solver = new Solver1427B();
    int n = in.nextInt();
    for (int i = 0; i < n; i++) {
      solver.solve(i, in, out);
    }
    out.close();
  }

  static class Solver1427B {
    public void solve(int testNumber, InputReader in, PrintWriter out) {
      //out.println("start");
      int n = in.nextInt(), k = in.nextInt();
      String s = in.next();
      List<Integer> list = new ArrayList<>();
      boolean hasWin = false;
      int lc = 0;
      int res = 0;
      int firstlc = 0;
      int lastlc = 0;
      for (int i = 0; i < s.length(); i++) {
        char c = s.charAt(i);
        if (c == 'L') {
          lc++;
        } else if (c == 'W') {
          if (lc > 0) {
            if (hasWin) {
              list.add(lc);
              //out.println("lc: " + lc);
            } else {
              firstlc = lc;
            }
          }
          lc = 0;
          hasWin = true;
          if (i > 0 && s.charAt(i - 1) == 'W') {
            res += 2;
          } else {
            res++;
          }
        }
      }

      if (k == 0) {
        out.println(res);
        return;
      }

      if (hasWin) {
        lastlc = lc;
      } else {
        out.println(k * 2 - 1);
        return;
      }

      //out.println("firstlc: " + firstlc);
      //out.println("lastlc: " + lastlc);

      Collections.sort(list);
      int i = 0;
      for (i = 0; i < list.size(); i++) {
        int tmp = list.get(i);
        if (k >= tmp) {
          res += (tmp * 2 + 1);
          k -= tmp;
        } else {
          break;
        }
      }

      if (i < list.size()) {
        res += 2 * k;
      } else {
        res += 2 * Math.min(k, firstlc + lastlc);
      }
      out.println(res);
    }
  }

