		TreeMap<Integer, Integer> map = new TreeMap<>();
		for (int i = lastPos; i >= firstPos; --i) {
			char c = s.charAt(i);
			if (c == 'L') {
				lSeq[i] = 1 + lSeq[i + 1];
				map.put(lSeq[i], map.getOrDefault(lSeq[i], 0) + 1);
				map.put(lSeq[i] - 1, map.getOrDefault(lSeq[i] - 1, 0) - 1);
			}
		}

		if (map.containsKey(0)) {
			map.remove(0);
		}

		for (int x : map.keySet()) {
			int gap = x;
			int f = map.get(x);
			int spaces = x * f;
			if (k >= spaces) {
				int o = gap * 2 + 1;
				o = o * f;
				sum += o;
				k -= spaces;
				win += spaces;
			} else {
				int t = k;
				t = t / gap;
				spaces = t * gap;
				k -= spaces;
				int o = gap * 2 + 1;
				o = o * t;
				sum += o;
				win += spaces;
				break;
			}
		}

		for (int i = 0; i < firstPos && firstPos != s.length() && k > 0; ++i) {
			sum += 2;
			k--;
		}

		for (int i = lastPos + 1; i < s.length() && k > 0 && lastPos != -1; ++i) {
			sum += 2;
			k--;
		}

		if (k > 0) {
			sum = sum + (k * 2);

		}
		return sum;

	}

}

