@tuplerize
def solve(k, games):
    wins = 0
    loses = 0
    win_streaks = 0
    gaps = []

    for i in range(len(games)):
        if games[i] == 'W':
            wins += 1
            if i == 0 or games[i-1] == 'L':
                win_streaks += 1
        else:
            loses += 1
            if i == 0 or games[i-1] == 'W':
                gaps.append(0)

            gaps[-1] += 1

    if k >= loses:
        return 2 * (len(games)) - 1

    if wins == 0:
        if k == 0:
            return 0

        return 2 * k - 1

    if games[0] == 'L':
        gaps[0] = float('inf')

    if games[-1] == 'L':
        gaps[-1] = float('inf')



    gaps = sorted(gaps)
    wins += k
    for gap in gaps:
        if gap > k:
            break

        k -= gap
        win_streaks -= 1


    return 2 * wins - win_streaks

