public class mC {


    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        StringBuilder st = new StringBuilder();
        int t = sc.nextInt();

        for (int test = 0; test < t; test++) {
            int n = sc.nextInt();
            int k = sc.nextInt();
            String s = sc.next();
            ArrayList<Integer> gap = new ArrayList<>();
            int initialScore = 0;
            int beforeW = 0;
            boolean hasBegun = false;
            int lastW = 0;
            int loses = 0;
            if (s.substring(0,1).equals("W")) {
                initialScore+=1;
                hasBegun = true;

            } else {
                loses+=1;
                beforeW++;
                lastW++;
            }

            for (int i=1;i<n;i++) {
                if (s.substring(i,i+1).equals("W")) {
                    if (lastW==0) {
                        initialScore+=2;
                    } else {
                        initialScore+=1;
                        if (hasBegun==false) {
                            hasBegun=true;
                            lastW=0;
                        } else {
                            gap.add(lastW);
                            lastW=0;
                        }
                    }

                } else {
                    loses++;
                    if (hasBegun==false) {
                        beforeW++;
                        lastW++;
                    } else {
                        lastW++;
                    }
                }
            }
            Collections.sort(gap);
            if (k==0) {
                st.append(initialScore+"\n");
            } else if (k+beforeW+lastW>=loses) {
                st.append(Math.min(2*(n-loses+k)-1,2*(n)-1)+"\n");

            } else {
                for (int j=0;j<gap.size();j++) {
                    int u = gap.get(j);
                    if (k>=u) {
                        initialScore+=2*u+1;
                        k-=u;
                    } else {
                        initialScore+=2*k;
                        k=0;
                    }
                }
                st.append(initialScore+"\n");
            }

        }

        System.out.print(st.toString());

    }

    public static int findNthInArray(int[] arr,int val,int start,int o) {
        if (o==0) {
            return start-1;
        } else if (arr[start] == val) {
            return findNthInArray(arr,val,start+1,o-1);
        } else {
            return findNthInArray(arr,val,start+1,o);
        }
    }
    public static ArrayList<Integer> dfs(int at,ArrayList<Integer> went,ArrayList<ArrayList<Integer>> connect) {
        for (int i=0;i<connect.get(at).size();i++) {
            if (!(went.contains(connect.get(at).get(i)))) {
                went.add(connect.get(at).get(i));
                went=dfs(connect.get(at).get(i),went,connect);
            }
        }
        return went;
    } public static int[] bfs (int at, int[] went, ArrayList<ArrayList<Integer>> queue, int numNodes, ArrayList<ArrayList<Integer>> connect) {
        if (went[at]==0) {
            went[at]=queue.get(numNodes).get(1);
            for (int i=0;i<connect.get(at).size();i++) {
                if (went[connect.get(at).get(i)]==0) {
                    ArrayList<Integer> temp = new ArrayList<>();
                    temp.add(connect.get(at).get(i));
                    temp.add(queue.get(numNodes).get(1)+1);
                    queue.add(temp);
                }
            }

        }
        if (queue.size()==numNodes+1) {
            return went;
        } else {
            return bfs(queue.get(numNodes+1).get(0),went, queue, numNodes+1, connect);
        }
    }
    public static long fastPow(long base,long exp,long mod) {
        if (exp==0) {
            return 1;
        } else {
            if (exp % 2 == 1) {
                long z = fastPow(base,(exp-1)/2,mod);
                return ((((z*base) % mod) * z) % mod);
            } else {
                long z = fastPow(base,exp/2,mod);
                return ((z*z) % mod);
            }
        }
    }
    public static long fastPow(long base,long exp) {
        if (exp==0) {
            return 1;
        } else {
            if (exp % 2 == 1) {
                long z = fastPow(base,(exp-1)/2);
                return ((((z*base)) * z));
            } else {
                long z = fastPow(base,exp/2);
                return ((z*z));
            }
        }
    }
    public static int firstLarger(int val,ArrayList<Integer> ok,int left,int right) {
        if (ok.get(right)<=val) {
            return -1;
        }
        if (left==right) {
            return left;
        } else if (left+1==right) {
            if (val<ok.get(left)) {
                return left;
            } else {
                return right;
            }
        } else {
            int mid = (left+right)/2;
            if (ok.get(mid)>val) {
                return firstLarger(val,ok,left,mid);
            } else {
                return firstLarger(val,ok,mid+1,right);
            }
        }
    }
}

