    private static void solve(int k, String line) {
        int prev = 0, sum = 0, curl = 0, firstl = 0;
        List<Integer> l2 = new ArrayList<>();
        boolean hasW = false;

        for (char c : line.toCharArray()) {
            if (c == 'L') {
                curl++;
                prev = 0;
                continue;
            }

            sum += prev + 1;
            if (curl != 0) {
                if (hasW) l2.add(curl);
                else firstl = curl;
            }

            hasW = true;
            curl = 0;
            prev = 1;
        }

        Collections.sort(l2);
        for (int i = 0; i < l2.size() && k > 0; i++) {
            if (k >= l2.get(i)) {
                int v = l2.get(i);
                sum += v*2 + 1;
                k -= v;
            } else {
                int v = k;
                sum += v*2;
                k = 0;
            }
        }

        int vv = Math.min(k, curl);
        k -= vv;
        if (vv > 0) sum += hasW ? 2*vv : ((vv - 1)*2 + 1);

        vv = Math.min(k, firstl);
        k -= vv;
        if (vv > 0) sum += 2*vv;

        System.out.println(sum);
    }

    private static int[] readArr(String line, int len) {
        int[] res = new int[len];
        int last = 0, pos = 0, min = 1;

        for (char c : line.toCharArray()) {
            if (c == ' ') {
                res[pos++] = min*last;
                last = 0;
                min = 1;
            } else if (c == '-') {
                min = -1;
            } else {
                last = last*10 + c - '0';
            }
        }

        res[pos] = min*last;
        return res;
    }
}

