	public void solution(String s,int k){
		int res=0;
		PriorityQueue<Integer> pq=new PriorityQueue<>();
		List<Integer>list=new ArrayList<>();
		for(int i=0;i<s.length();i++){
			char c=s.charAt(i);
			if(c=='W'){
				if(i==0)res++;
				else{
					if(s.charAt(i-1)=='W')res+=2;
					else res++;
				}
				list.add(i);
			}
		}

		if(res==0){
			int fill=Math.min(s.length(),k);
			res=fill*2-1;
			msg(Math.max(res,0)+"");
			return;
		}

		//msg(res+"");

		for(int i=1;i<list.size();i++){
			if(list.get(i)-list.get(i-1)>1){
				int gap=list.get(i)-list.get(i-1)-1;
				pq.add(gap);
			}
		}



		while(pq.size()>0&&k>0){
			int gap=pq.poll();
			if(k>=gap){
				k-=gap;
				res+=(gap*2+1);
			}else{
				res+=(k*2);
				k=0;
			}
		}


		if(k>0){
			int first=list.get(0)-0;
			//msg("first  "+first);
			if(k>=first){
				res+=(first*2);
				k-=first;
			}else{
				res+=(k*2);
				k=0;
			}
		}

		if(k>0){
			int last=(s.length()-1)-list.get(list.size()-1);
			//msg("last  "+last);
			if(k>=last){
				res+=(last*2);
				k-=last;
			}else{
				res+=(k*2);
				k=0;
			}
		}


		msg(res+"");

	}





