public class ChessCheater {
    public static void main(String[] args) throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));
        StringBuilder sb = new StringBuilder();

        int cases = Integer.parseInt(reader.readLine());

        String input;
        int moves;
        List<Integer> gaps;
        int currentGap;

        for (int n = 0; n < cases; n++ ) {

            moves = Integer.parseInt(reader.readLine().split(" ")[1]);
            input = reader.readLine();
            gaps = new ArrayList<>();
            currentGap = 0;

            int first = input.indexOf('W');
            int last = input.lastIndexOf('W');
            int endGaps = first + ((input.length() - 1) - last);

            boolean consecutive = false;

            int score = 0;

            if (first == -1) { //no wins
                int spaces = Math.min(input.length(), moves);

                if (spaces == 0) {
                    sb.append(0).append('\n');
                }
                else if (spaces == 1) {
                    sb.append(1).append('\n');
                } else {
                    sb.append(spaces * 2 - 1).append('\n');
                }
            } else {
                for (int i = first; i < last + 1; i++) {

                    if (consecutive && input.charAt(i) == 'W') {
                        score += 2;
                    } else if (input.charAt(i) == 'W') {
                        consecutive = true;
                        score++;
                    }

                    if (input.charAt(i) == 'L') {
                        currentGap++;
                        consecutive = false;
                    } else if (currentGap != 0) {
                        gaps.add(currentGap);
                        currentGap = 0;
                    }
                }

                if (currentGap != 0) {
                    gaps.add(currentGap);
                }

                //Collections.sort(gaps, (a,b) -> Integer.compare(b,a));

                Collections.sort(gaps);
                for (int i = 0; i < gaps.size() && moves > 0; i++) {
                    currentGap = gaps.get(i);

                    if (moves >= currentGap) {
                        score += currentGap * 2 + 1;
                        moves -= currentGap;
                    } else {
                        endGaps += currentGap;
                    }
                }

                int spaces = Math.min(moves, endGaps);
                score += spaces * 2;

                sb.append(score).append('\n');


/*                System.out.println(gaps.toString());
                System.out.println("end spaces: " + endGaps);
                //System.out.println("score: " + score);
                System.out.println("moves: " + moves);*/
            }




        }

        System.out.println(sb.toString());
    }
}

