  for (long long(i) = (0); (i) <= (n - 1); ++(i))
    if (s[i] == 'W') {
      fw = i;
      break;
    }
  for (long long(i) = (n - 1); (i) >= (0); --(i))
    if (s[i] == 'W') {
      lw = i;
      break;
    }
  vector<pair<long long, long long>> cnt;
  long long cur = 0, pos;
  for (long long(i) = (fw); (i) <= (lw); ++(i)) {
    if (s[i] == 'W') continue;
    if (!cur) pos = i;
    ++cur;
    if (i + 1 <= lw && s[i + 1] == 'L') continue;
    cnt.push_back({cur, pos});
    cur = 0;
  }
  sort((cnt).begin(), (cnt).end());
  for (auto&(e) : (cnt))
    for (long long(i) = (e.second); (i) <= (e.first + e.second - 1); ++(i))
      if (k) {
        --k;
        s[i] = 'W';
      }
  if (fw != -1)
    for (long long(i) = (fw - 1); (i) >= (0); --(i))
      if (k) {
        s[i] = 'W';
        --k;
      }
  if (lw != -2)
    for (long long(i) = (lw + 1); (i) <= (n - 1); ++(i))
      if (k) {
        s[i] = 'W';
        --k;
      }
  for (long long(i) = (0); (i) <= (n - 1); ++(i))
    if (s[i] == 'L' && k) {
      s[i] = 'W';
      --k;
    }
  long long ans = (s[0] == 'W');
  for (long long(i) = (1); (i) <= (n - 1); ++(i))
    if (s[i] == 'W')
      if (s[i - 1] == 'W')
        ans += 2;
      else
        ++ans;
  cout << ans;
