	public static void main(String[] args) {
		FastScanner sc = new FastScanner();
		int t = sc.nextInt();
		while(t -- > 0) {
			int n = sc.nextInt();
			int k = sc.nextInt();
			char[] s = sc.next().toCharArray();
			int exts = 0;
			int p = 0;
			while(p < n && s[p] == 'L') {
				exts++; p++;
			}
			if(p == n) {
				if(k >= n) System.out.println(2*n-1);
				else System.out.println(Math.max(2*k-1, 0));
				continue;
			}
			PriorityQueue<Integer> q = new PriorityQueue<>();
			int count = 0;
			int res = 0;
			for(int i = p; i < n; i++) {
				if(s[i] == 'W') {
					q.add(count); count = 0;
					res++;
					if(i > 0 && s[i-1] == 'W') res++;
				}
				else {
					count++;
				}
			}
			exts += count;
			while(k > 0 && !q.isEmpty()) {
				int c = q.poll();
				if(c == 0) continue;
				if(k >= c) {
					k -= c;
					res += c*2 + 1;
				}
				else {
					res += k*2; 
					k = 0; break;
				}
			}
			if(k > 0) {
				if(k >= exts) {
					res += 2*exts;
				}
				else {
					res += 2*k;
				}
			}
			System.out.println(res);
		}

	}

