using namespace std;
long long n, k;
string s;
void solve(long long n, long long k, string s) {
  vector<long long> v;
  long long win = 0;
  for (long long i = 0; i < n; i++) {
    if (s[i] == 'W') {
      win++;
    }
  }
  if (win == 0 and k != 0) {
    cout << (2 * k) - 1 << endl;
    return;
  } else if (win == 0 and k == 0) {
    cout << 0 << endl;
    return;
  }
  win += k;
  if (win >= n) {
    cout << (2 * n) - 1 << endl;
    return;
  }
  long long l = 0;
  long long j = 0;
  while (j < n) {
    if (s[j] == 'L') {
      l++;
    } else {
      v.push_back(l);
      l = 0;
    }
    j++;
  }
  if (l != 0) {
    v.push_back(l);
  }
  if (s[0] == 'L') {
    v.erase(v.begin());
  }
  if (s[n - 1] == 'L') {
    v.erase(v.begin() + v.size() - 1);
  }
  sort(v.begin(), v.end());
  long long kk = 0;
  long long i;
  for (i = 0; i < v.size(); i++) {
    if (kk + v[i] > k) {
      break;
    }
    kk += v[i];
  }
  long long notchangedstreaks = v.size() - i;
  cout << 2 * win - notchangedstreaks - 1 << endl;
}
int main() {
  long long t;
  cin >> t;
  while (t--) {
    cin >> n >> k;
    cin >> s;
    solve(n, k, s);
  }
  return 0;
}

