using namespace std;
class R {
 public:
  long long s;
  long long e;
  long long val;
  long long dir;
};
bool mycmp(R a, R b) {
  if (a.val == b.val) {
    return (a.e - a.s + 1) < (b.e - b.s + 1);
  }
  return a.val > b.val;
}
int main() {
  long long t;
  ios_base::sync_with_stdio(false);
  cin.tie(NULL);
  cin >> t;
  while (t--) {
    long long n, k;
    cin >> n >> k;
    string s;
    cin >> s;
    long long cnt = 0;
    for (int i = 0; i < n; i++) {
      if (s[i] == 'L') {
        cnt++;
      }
    }
    if (cnt > k && cnt < n) {
      vector<R> v;
      long long idx = 0;
      while (idx < n) {
        if (s[idx] == 'L') {
          R r;
          r.s = idx;
          r.e = idx;
          while (idx + 1 < n && s[idx + 1] == 'L') {
            r.e = idx + 1;
            idx++;
          }
          r.dir = 1;
          long long diff = r.e - r.s + 1;
          if (r.s == 0) {
            r.val = 0;
          } else if (r.e == n - 1) {
            r.val = 0;
            r.dir = 0;
          } else {
            r.val = 1;
          }
          v.push_back(r);
          idx++;
        } else {
          idx++;
        }
      }
      sort(v.begin(), v.end(), mycmp);
      while (k > 0) {
        for (auto vv : v) {
          if (vv.dir == 1) {
            for (int i = vv.e; i >= vv.s && k > 0; i--) {
              s[i] = 'W';
              k--;
            }
          } else {
            for (int i = vv.s; i <= vv.e && k > 0; i++) {
              s[i] = 'W';
              k--;
            }
          }
        }
      }
    }
    if (k > 0) {
      for (int i = 0; i < n && k > 0; i++) {
        if (s[i] == 'L') {
          s[i] = 'W';
          k--;
        }
      }
    }
    long long score = 0;
    if (s[0] == 'W') {
      score = 1;
    }
    for (int i = 1; i < n; i++) {
      if (s[i - 1] == 'W' && s[i] == 'W') {
        score += 2;
      } else if (s[i] == 'W') {
        score += 1;
      }
    }
    cout << score << endl;
  }
  return 0;
}

