public class Solution extends PrintWriter {


    private void solve()  {
    	int t = sc.nextInt();
    	for(int tt = 0; tt < t; tt++) {
    		int n = sc.nextInt();
    		int k = sc.nextInt();
    		char[] chs = sc.nextString().toCharArray();
    		ArrayList<int[]> gaps = new ArrayList<>();
    		for(int i = 0; i < n; ) {
    			if(chs[i] == 'W') {
    				i++;
    				continue;
    			} else {
    				int j = i;
    				while(j < n && chs[j] == 'L') j++;
    				if(i == 0 || j == n) gaps.add(new int[] {i,j-1,1<<30});
    				else gaps.add(new int[] {i,j-1,j-i});
    				i = j;
    			}
    		}
    		Collections.sort(gaps, Comparator.comparing((int[] arr) -> arr[2]));
    		for(int i = 0; i < gaps.size() && k > 0; i++) {
    			int[] gap = gaps.get(i);
    			if(gap[0] == 0) {
        			for(int j = gap[1]; j >= gap[0] && k > 0; j--) {
        				chs[j] = 'W';
        				k--;
        			}
    			} else {
        			for(int j = gap[0]; j <= gap[1] && k > 0; j++) {
        				chs[j] = 'W';
        				k--;
        			}
    			}
    		}
    		int ans = 0;
    		for(int i = 0; i < n; i++) {
    			if(chs[i] == 'W') {
    				if(i > 0 && chs[i-1] == 'W') ans += 2;
    				else ans++;
    			}
    		}
    		println(ans);
    	}
    }

