public class B {
	public static void main(String[] args) {
		Scanner input = new Scanner(System.in);

		int t = input.nextInt();
		while (t-- > 0) {
			int n = input.nextInt();
			int k = input.nextInt();

			String s = input.next();

			List<LossRun> lossRuns = new ArrayList<>();
			for (int i = 0; i < s.length();) {
				if (s.charAt(i) == 'L') {
					int startPosition = i;
					while(i < s.length() && s.charAt(i) == 'L') {
						++i;
					}
					int runLength = i - startPosition;
					lossRuns.add(new LossRun(startPosition, runLength, n));
				} else if (s.charAt(i) == 'W') {
					while (i < s.length() && s.charAt(i) == 'W') {
						++i;
					}
				} else {
					System.out.println("Program is mistake");
					return;
				}
			}

			Collections.sort(lossRuns);

			int remainingCheats = k;
			StringBuilder mutableS = new StringBuilder(s);
			for (LossRun lossRun: lossRuns) {
				// System.out.println(lossRun);
				int replaceLength = Math.min(lossRun.length, remainingCheats);

				int startPosition;
				int endPosition;
				if (lossRun.isFirst()) {
					endPosition = lossRun.position + lossRun.length;
					startPosition = endPosition - replaceLength;
				} else {
					startPosition = lossRun.position;
					endPosition = startPosition + replaceLength;
				}

				for (int i = startPosition; i < endPosition; ++i) {
					mutableS.setCharAt(i, 'W');
				}
				remainingCheats -= replaceLength;
			}

			String cheatedS = mutableS.toString();
			// System.out.println(cheatedS);

			// calculate the new value here
			System.out.println("" + totalScore(cheatedS));

		}
	}

	private static int totalScore(String matchOutcome) {
		int totalScore = matchOutcome.charAt(0) == 'W' ? 1 : 0;

		for (int i = 1; i < matchOutcome.length(); ++i) {
			if (matchOutcome.charAt(i) == 'W') {
				if (matchOutcome.charAt(i - 1) == 'W') {
					totalScore += 2;
				} else {
					totalScore += 1;
				}
			}
		}

		return totalScore;
	}
}

class LossRun implements Comparable {
	int position;
	int length;
	int totalGames;

	LossRun(int position, int length, int totalGames) {
		this.position = position;
		this.length = length;
		this.totalGames = totalGames;
	}

	public boolean isFirst() {
		return this.position == 0;
	}

	public boolean isLast() {
		return this.position + length == totalGames;
	}

	public boolean isFirstOrLast() {
		return this.isFirst() || this.isLast();
	}

	@Override
	public int compareTo(Object o) {
		LossRun other = (LossRun) o;
		if (this.isFirstOrLast() && !other.isFirstOrLast()) {
			return 1;
		} else if (!this.isFirstOrLast() && other.isFirstOrLast()) {
			return -1;
		} else {
			return this.length - other.length;
		}
	}

	@Override
	public String toString() {
