import java.util.*;
import static java.lang.Math.max;
import static java.lang.Math.min;

public class Main {
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        List<Integer> list = new ArrayList<>();
        //多次问询
        int t = in.nextInt();
        for(int i=0;i<t;i++){
            int n = in.nextInt();
            int k = in.nextInt();
            in.nextLine();
            String s = in.nextLine();
            list.add(scoreMax(n,k,s));
        }

        for (Integer i:list) {
            System.out.println(i);
        }
    }

    private static int scoreMax(int n, int k, String s) {
        int lenS = s.length();
        char[] chars = s.toCharArray();

        //count赋值，则只会计算双W间的连续L
        //开头L与末尾L，都是只增加2
        int count = lenS;
        int score = 0;
        List<Integer> list = new ArrayList<>();

        //W之间的L长度，没有末尾L长度
        for(int i=0;i<lenS;i++){
            if(chars[i]=='L'){
                ++count;
            }else {
                if(i>0&&chars[i-1]=='W'){
                    score+=2;
                }else {
                    score+=1;
                }
                if(count>0) {
                    list.add(count);
                }
                count=0;
            }
        }

        if(count == lenS*2){
            //说明全是L
            return max(0,2*min(n,k)-1);
        }

        Collections.sort(list);
        for (Integer i:list) {
            if(k>=i){
                score+=i*2+1;
                k-=i;
            }
        }
        score+=2*k;
        //不能超过最大值
        return min(score,2*n-1);
    }

}

