import java.awt.*;
import java.io.*;
import java.util.*;
import java.util.List;

public class Coding {


    static long MOD = 1000000007;
    static long[] fact = new long[302];
    static long[] invFact = new long[302];
    static long[][][] segmentTree = new long[4*300000 + 11][2][2];
    static long MINVALUE = Integer.MIN_VALUE;


    public static void main(String[] args) {
        BufferedReader bi = new BufferedReader(new InputStreamReader(System.in));
        BufferedWriter writer = new BufferedWriter(new OutputStreamWriter(System.out));
        //precompute();
        try {
            int t = Integer.parseInt(bi.readLine());
            while (t > 0) {
                String[] input = bi.readLine().split(" ");
                int n = Integer.parseInt(input[0].trim());
                int k = Integer.parseInt(input[1].trim());
                String s = bi.readLine();
                int loss = 0;
                int ans = 0;
                boolean anyW = false;
                int[] points = new int[n];
                for(int i=0;i<s.length();++i) {
                    if (s.charAt(i)=='L') {
                        loss++;
                        if (i==0) {
                            points[i] = 0;
                        } else {
                            points[i] = points[i-1];
                        }
                    } else {
                        anyW = true;
                        if (i==0) {
                            ans++;
                            points[0] = 1;
                        } else if (s.charAt(i-1)=='W') {
                            ans+=2;
                            points[i] = points[i-1]+2;
                        } else {
                            ans++;
                            points[i] = points[i-1] +1;
                        }
                    }
                }
                //System.out.println("Initial ans is " + ans);
                if (loss<=k) {
                    writer.append((2*n-1) + "\n");
                } else {
                    int increase = 0;
                    List<Integer> clusters = new ArrayList<>();
                    int prev = -1;
                    for(int i=0;i<n;++i) {
                        if (s.charAt(i)=='W') {
                            if (prev==-1) {
                            } else {
                                if (prev<i-1) {
                                    clusters.add(i-prev-1);
                                }
                            }
                            prev = i;
                        }
                    }
                    Collections.sort(clusters);
                    //System.out.println(clusters);
                    for(int i=0;i<clusters.size();++i) {
                        if (clusters.get(i)<=k) {
                            k-=clusters.get(i);
                            ans+=(2*clusters.get(i) + 1);
                        } else {
                            ans+=(2*k);
                            k=0;
                            break;
                        }
                    }
                    if (anyW) {
                        ans += 2 * k;
                    } else {
                        if (k>0) {
                            ans += (2 * k - 1);
                        }
                    }
                    writer.append((ans+increase) + "\n");
                }
                writer.flush();
                 t--;
            }
            writer.flush();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private static int gcd(int a, int b) {
        if (a<b) {
            return gcd(b, a);
        }
        if (a%b==0) {
            return b;
        }
        return gcd(b, a%b);
    }

    private static void dfs(int cur, List<Integer>[] adj, int[] nodeCount, int[] maxChild,  int parent, int[] par) {
        par[cur] = parent;
        for(int i=0;i<adj[cur].size();++i) {
            int nxt = adj[cur].get(i);
            if (nxt!=parent) {
                dfs(nxt, adj, nodeCount, maxChild, cur, par);
                nodeCount[cur]+=nodeCount[nxt];
                maxChild[cur] = Math.max(maxChild[cur], nodeCount[nxt]);
            }
        }
    }

    private static int findDist(int a, int b, int[] par) {
        Map<Integer, Integer> dist = new HashMap<>();
        int count = 0;
        dist.put(a, 0);
        while (par[a]!=-1) {
            a = par[a];
            count++;
            dist.put(a, count);
        }
        count=0;
        while(!dist.containsKey(b)) {
            b = par[b];
            count++;
        }
        return count+dist.get(b);
    }

    private static int findLongestPath(int cur, int prev, List<Integer>[] adj, int[] dep) {
        int diameter = 0;
        int highest = -1, second=-1;
        for(int i=0;i<adj[cur].size();++i) {
            int nxt = adj[cur].get(i);
            if (nxt!=prev) {
                diameter = Math.max(diameter, findLongestPath(nxt, cur, adj, dep));
                if (dep[nxt]>highest) {
                    second = highest;
                    highest = dep[nxt];
                } else if (dep[nxt]>second) {
                    second = dep[nxt];
                }
            }
        }
        diameter = Math.max(diameter, highest + second + 2);
        return diameter;
    }

    static long ncr(int n, int r) {
        long num = fact[n];
        long denom = (invFact[r]*invFact[n-r])%MOD;
        //System.out.println("num is " + num + " denom is" + denom);
        return (num*denom)%MOD;
    }

    static long modExp(long a, long b, long mod) {
        //System.out.println("a is " + a + " and b is " + b);
        if (a==1) return 1;
        long ans = 1;
        while (b!=0) {
            if (b%2==1) {
                ans = (ans*a)%mod;
            }
            a = (a*a)%mod;
            b/=2;
        }
        return ans;
    }

    private static void precompute() {
        fact[0] = 1;
        invFact[0] = 1;
        for(int i=1;i<=300000;++i) {
            fact[i] = (fact[i-1]*i)%MOD;
            invFact[i] = modExp(fact[i], MOD-2, MOD);
        }
    }
}

