            k -= 1
            L -= 1
    ans = 1 if s[0] == 'W' else 0
    for i in range(1, n):
        if s[i] == 'W':
            if s[i - 1] == 'W':
                ans += 2
            else:
                ans += 1

    lose = []
    c = 0
    for i in range(s.index('W') + 1, rindex(s, 'W') + 1):
        if s[i] == 'W':
            if c:
                lose.append(c)
                c = 0
        else:
            c += 1
    lose.sort(reverse=True)
    while k and lose:
        c = lose.pop()
        if k >= c:
            ans += c * 2 + 1
            k -= c
            L -= c
        else:
            ans += k * 2
            k = 0
    ans += min(k, L) * 2
    return ans


