import java.util.*;
public class ChessCheater {
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        int T = in.nextInt();
        for (int t = 0; t < T; t++) {
            int N = in.nextInt(), K = in.nextInt();
            String s = in.next();
            boolean[] arr = new boolean[N];
            for (int i = 0; i < N; i++) {
                arr[i] = s.charAt(i) == 'W';
            }
            Queue<Integer> groups = new PriorityQueue<>();
            int extra = 0, start = 0;
            for (int i = 0; i < N; i++) {
                if (!arr[i]) {
                    int j = i;
                    while (j < N && !arr[j]) {
                        j++;
                    }
                    if (i > 0 && j < N) {
                        groups.add(j - i);
                    } else {
                        extra += (j - i);
                    }
                    i = j - 1;
                } else {
                    int j = i;
                    while (j < N && arr[j]) {
                        j++;
                    }
                    start += (j - i) * 2 - 1;
                    i = j - 1;
                }
            }
            int res = 0;
            if (extra == N && K > 0) {
                res = K * 2 - 1;
            } else {
                while (!groups.isEmpty() && groups.peek() <= K) {
                    int n = groups.poll();
                    res += n * 2 + 1;
                    K -= n;
                }
                if (!groups.isEmpty()) {
                    res += K * 2;
                    K = 0;
                }
                if (K > 0) {
                    res += Math.min(K, extra) * 2;
                }
            }
            System.out.println(start + res);
        }
    }
}

