                    counter += 2*l+1;
                }else {
                    counter += 2*tmp;
                }
                i++;
            }
            if (k>0){
                if (!hasW){
                    counter = 2*k-1;
                }else{
                    int z = lofHead+lofL;
                    int tmp = k;
                    k -= z;
                    if (k>0){
                        counter += 2*z;
                    }else {
                        counter += 2*tmp;
                    }
                }
            }
            System.out.println(counter);
        }
    }
    public static int valueD(int i){
        if (checkF(i)&&checkL(i)){
            return 3;
        }else if (checkF(i)||checkL(i)){
            return 2;
        }else {
            return 1;
        }
    }
    public static boolean checkF(int i){
        if (i>0){
            if (s.charAt(i-1)=='W'){
                return true;
            }
        }
        return false;
    }
    public static boolean checkL(int i){
        if (i<s.length()-1){
            if (s.charAt(i+1)=='W'){
                return true;
            }
        }
        return false;
    }
}

