    while(x>0)
    {
        if(x%2!=0)s.push(1);
        else s.push(0);
        x/=2;
        dem++;
    }
    while(dem<sl){s.push(0);dem++;}
    while(!s.empty()){cout<<s.top();s.pop();}
    cout<<endl;
}

string intostring(ll a)
{
    stack<int> st;
    while(a>0)
    {
        st.push(a%10);
        a/=10;
    }
    string s;
    while(!st.empty())
    {
        s+=(char)(st.top()+'0');st.pop();
    }
    return s;
}

