  if(n < r) return 0L ; 

    long num = factorial[(int)(n)] ;

    long den = (factorial[(int)(r)]*factorial[(int)(n-r)]) %  p ;


    long ans  = num*(modInverse(den,p)) ;

    ans = ans % p ;

    return ans  ;


}


