  static class BLordOfTheValues {
    public final void solve(int testNumber, InputReader in, PrintWriter out) {
      int n = in.nextInt();
      int[] a = in.nextIntArrayOneBased(n);
      out.println(n * 3);
      for (int i = 1; i <= n / 2; i++) {
        int j = i + n / 2;
        out.println(2 + " " + i + " " + j);
        out.println(1 + " " + i + " " + j);
