	public static ArrayList<Integer> soe() {
		int size = (int) (1e6);
		ArrayList<Integer> list = new ArrayList<>();
		boolean[] arr = new boolean[size + 10];
		for (int i = 2; i * i <= size; i++) {
			if (arr[i])
				continue;
			for (int j = 2; j * i <= size; j++) {
				arr[j * i] = true;
			}
		}
		for (int i = 2; i <= size; i++) {
			if (!arr[i])
				list.add(i);

		}
		return list;
	}

	public static long gcd(long a, long b) {
		if (a < b) {
			return gcd(b, a);
		}
		if (b == 0)
			return a;
		return gcd(b, a % b);

	}

	public static long pow(long a, long x, long mod) {
		if (x == 0)
			return 1l;
		if (x == 1)
			return a;
		long p = pow(a, x / 2, mod);
		if (x % 2 == 1) {
			return (((p * p) % mod) * a) % mod;
		} else {
			return (p * p) % mod;
		}

	}

	static int LowerBound(int arr[], int item) {
		int n = arr.length;
		int i = 0, j = n - 1;
		int ans = -1;
		while (i <= j) {
			int mid = (i + j) / 2;
			if (arr[mid] < item) {
				i = mid + 1;
			} else {
				ans = mid;
				j = mid - 1;

			}

		}
		return ans;
	}

	static int UpperBound(int arr[], int item) {
		int n = arr.length;
		int i = 0, j = n - 1;
		int ans = -1;
		while (i <= j) {
			int mid = (i + j) / 2;
			if (arr[mid] <= item) {
				ans = mid;
				i = mid + 1;
			} else {
				j = mid - 1;

			}

		}
		return ans;
	}

}

