    {
      H[k] = '1';
    }
  }
  if (dp[n - 1] > 0 and dp[n - 1] < n)
  {
    int best  = n - 1 - dp[n - 1] + 1;
    for (int j = best ; j < best + m and  j < n; j++)
    {
      H[j] = '1';
    }
  }
