public class P2 {
    private static class Pair<K, V>{
        K k;
        V v;

        Pair(K k, V v){
            this.k = k;
            this.v = v;
        }

        public K getKey() {
            return k;
        }

        public V getValue() {
            return v;
        }
    }

    private static class Disj{
        private int[] parent;
        private int components;
        Disj(int n){
            parent = new int[n];
            components = n;
            make_set();
        }

        private void make_set(){
            for (int i = 0; i < parent.length; i++) {
                parent[i] = i;
            }
        }

        public void union(int a, int b) {
            a = find(a);
            b = find(b);
            if(a != b) {
                parent[a] = b;
                components--;
            }
        }

        public int find(int x) {
            if(x != parent[x]){
                parent[x] = find(parent[x]);
            }

            return parent[x];
        }

        public int getComponentCount() {
            return components;
        }
    }

